// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

interface IIU2UGateway {
    // Events
    event ContractCall(
        address indexed sender,
        string destinationChain,
        string destinationContractAddress,
        bytes32 indexed payloadHash,
        bytes payload
    );

    event ContractCallWithToken(
        address indexed sender,
        string destinationChain,
        string destinationContractAddress,
        bytes32 indexed payloadHash,
        bytes payload,
        string symbol,
        uint256 amount
    );

    event ContractCallApproved(
        bytes32 indexed commandId,
        string sourceChain,
        string sourceAddress,
        address indexed contractAddress,
        bytes32 indexed payloadHash,
        bytes32 sourceTxHash,
        uint256 sourceEventIndex
    );

    event ContractCallApprovedWithMint(
        bytes32 indexed commandId,
        string sourceChain,
        string sourceAddress,
        address indexed contractAddress,
        bytes32 indexed payloadHash,
        string symbol,
        uint256 amount,
        bytes32 sourceTxHash,
        uint256 sourceEventIndex
    );

    event TokenSent(
        address indexed sender,
        string destinationChain,
        string destinationAddress,
        string symbol,
        uint256 amount
    );

    event Executed(bytes32 indexed commandId);

    // Core GMP functions
    function callContract(
        string calldata destinationChain,
        string calldata destinationContractAddress,
        bytes calldata payload
    ) external;

    function callContractWithToken(
        string calldata destinationChain,
        string calldata destinationContractAddress,
        bytes calldata payload,
        string calldata symbol,
        uint256 amount
    ) external;

    function sendToken(
        string calldata destinationChain,
        string calldata destinationAddress,
        string calldata symbol,
        uint256 amount
    ) external;

    // Validation functions
    function isContractCallApproved(
        bytes32 commandId,
        string calldata sourceChain,
        string calldata sourceAddress,
        address contractAddress,
        bytes32 payloadHash
    ) external view returns (bool);

    function isContractCallAndMintApproved(
        bytes32 commandId,
        string calldata sourceChain,
        string calldata sourceAddress,
        address contractAddress,
        bytes32 payloadHash,
        string calldata symbol,
        uint256 amount
    ) external view returns (bool);

    function validateContractCall(
        bytes32 commandId,
        string calldata sourceChain,
        string calldata sourceAddress,
        bytes32 payloadHash
    ) external returns (bool);

    function validateContractCallAndMint(
        bytes32 commandId,
        string calldata sourceChain,
        string calldata sourceAddress,
        bytes32 payloadHash,
        string calldata symbol,
        uint256 amount
    ) external returns (bool);

    // Chain management
    function addChain(string calldata chainName, uint256 chainId) external;
    function removeChain(string calldata chainName) external;
    function getChainId(string calldata chainName) external view returns (uint256);
    function getChainName(uint256 chainId) external view returns (string memory);

    // Admin functions
    function addWhitelistedRelayer(address relayer) external;
    function removeWhitelistedRelayer(address relayer) external;
    function isWhitelistedRelayer(address relayer) external view returns (bool);
}

contracts/IU2U.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {ERC20} from "@openzeppelin/contracts/token/ERC20/ERC20.sol";

contract IU2U is ERC20, Ownable {
    uint256 u2u_chainid = 2484; // 2484 is U2U Nebulas Testnet

    // Authorized gateway for cross-chain operations
    address public gateway;

    // Native U2U <-> IU2U conversion events
    event Deposited(address indexed user, uint256 amount);
    event Withdrawn(address indexed user, uint256 amount);

    constructor(address owner_)
        ERC20("Interoperable U2U", "IU2U")
        Ownable()
    {
        // Transfer ownership to the specified owner
        _transferOwnership(owner_);
    }

    modifier onlyU2UChain() {
        require(block.chainid == u2u_chainid, "Not U2U Chain");
        _;
    }

    /**
     * @dev Deposit native U2U tokens to mint equivalent IU2U tokens (1:1 ratio)
     *
     * This function allows users to lock their native U2U tokens in the contract
     * and receive an equivalent amount of IU2U tokens. This is the entry point
     * for users to participate in cross-chain transfers using the GMP protocol.
     *
     * Requirements:
     * - Must be called on U2U chain (chainId 2484 for testnet)
     * - Must send a positive amount of U2U (msg.value > 0)
     * - U2U tokens are locked in the contract and cannot be withdrawn by contract owner
     *
     * Process:
     * 1. Validates the sent U2U amount is greater than zero
     * 2. Mints equivalent IU2U tokens to the sender's address
     * 3. U2U tokens remain locked in the contract as collateral
     *
     * Example usage:
     * ```
     * // Deposit 100 U2U to get 100 IU2U
     * iu2u.deposit{value: 100 ether}();
     * ```
     *
     * @notice This creates a 1:1 backing between U2U and IU2U tokens
     * @notice Users can later withdraw their U2U by burning IU2U tokens
     */
    function deposit() public payable onlyU2UChain {
        require(msg.value > 0, 'Zero Value');

        address account = msg.sender;
        _mint(account, msg.value);

        emit Deposited(account, msg.value);
    }

    /**
     * @dev Withdraw native U2U tokens by burning equivalent IU2U tokens (1:1 ratio)
     *
     * This function allows users to redeem their locked U2U tokens by burning
     * their IU2U tokens. This is the exit mechanism for users who want to
     * convert their cross-chain IU2U tokens back to native U2U.
     *
     * Requirements:
     * - Must be called on U2U chain (chainId 2484 for testnet)
     * - User must have sufficient IU2U token balance
     * - Contract must have sufficient U2U balance to honor the withdrawal
     * - Amount must be greater than zero
     *
     * Security measures:
     * - Burns IU2U tokens before transferring U2U (checks-effects-interactions pattern)
     * - Uses low-level call for U2U transfer with success verification
     * - Prevents reentrancy by burning tokens first
     *
     * Process:
     * 1. Validates the withdrawal amount
     * 2. Checks user has sufficient IU2U balance
     * 3. Checks contract has sufficient U2U to transfer
     * 4. Burns the specified amount of IU2U tokens from user's balance
     * 5. Transfers equivalent U2U to the user
     * 6. Reverts entire transaction if U2U transfer fails
     *
     * Example usage:
     * ```
     * // Withdraw 50 U2U by burning 50 IU2U
     * iu2u.withdraw(50 ether);
     * ```
     *
     * @param amount_ The amount of IU2U tokens to burn and equivalent U2U to withdraw
     * @notice This maintains the 1:1 backing ratio between U2U and IU2U
     * @notice Failed U2U transfers will revert the entire transaction
     */
    function withdraw(uint256 amount_) public onlyU2UChain {
        address account = msg.sender;
        require(amount_ > 0, "Zero amount");
        require(address(this).balance >= amount_, "Not enough U2U");
        require(balanceOf(account) >= amount_, "Not enough IU2U");

        _burn(account, amount_);
        (bool success, ) = address(payable(account)).call{value: amount_}("");
        require(success, "Withdraw failed");

        emit Withdrawn(account, amount_);
    }

    /**
     * @dev Mint tokens (for cross-chain transfers from gateway)
     * @param to The address to mint tokens to
     * @param amount The amount of tokens to mint
     */
    function mint(address to, uint256 amount) external {
        require(msg.sender == owner() || msg.sender == gateway, "Unauthorized");
        require(amount > 0, "Amount must be greater than zero");
        require(to != address(0), "Invalid recipient");

        _mint(to, amount);
    }

    /**
     * @dev Burn tokens (for cross-chain transfers from gateway)
     * @param from The address to burn tokens from
     * @param amount The amount of tokens to burn
     */
    function burn(address from, uint256 amount) external {
        require(msg.sender == owner() || msg.sender == gateway, "Unauthorized");
        require(amount > 0, "Amount must be greater than zero");
        require(from != address(0), "Invalid sender");
        require(balanceOf(from) >= amount, "Insufficient balance");

        _burn(from, amount);
    }

    /**
     * @dev Get the current U2U backing ratio
     * @return The amount of U2U locked in the contract
     */
    function getU2UBalance() external view returns (uint256) {
        return address(this).balance;
    }

    /**
     * @dev Set the authorized gateway address
     * @param gateway_ The gateway contract address
     */
    function setGateway(address gateway_) external onlyOwner {
        require(gateway_ != address(0), "Invalid gateway address");
        gateway = gateway_;
    }

    /**
     * @dev Check if the contract is fully backed (U2U balance >= IU2U supply)
     * @return True if fully backed, false otherwise
     */
    function isFullyBacked() external view returns (bool) {
        return address(this).balance >= totalSupply();
    }

    receive() external payable {}
}

contracts/IU2UExecutable.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import {IIU2UGateway} from "./interfaces/IIU2UGateway.sol";
import {IExecutable} from "./interfaces/IExecutable.sol";

/**
 * @title IU2UExecutable
 * @dev Base contract for applications that want to receive GMP calls
 * Similar to Axelar's AxelarExecutable but for IU2U protocol
 */
abstract contract IU2UExecutable is IExecutable {
    IIU2UGateway public immutable gateway;

    error NotGateway();

    constructor(address gateway_) {
        if (gateway_ == address(0)) revert InvalidAddress();
        gateway = IIU2UGateway(gateway_);
    }

    modifier onlyGateway() {
        if (msg.sender != address(gateway)) revert NotGateway();
        _;
    }

    /**
     * @dev Execute a cross-chain contract call
     * @param commandId Unique identifier for the command
     * @param sourceChain Name of the source chain
     * @param sourceAddress Address of the sender on the source chain
     * @param payload The payload sent from the source chain
     */
    function execute(
        bytes32 commandId,
        string calldata sourceChain,
        string calldata sourceAddress,
        bytes calldata payload
    ) external override onlyGateway {
        bytes32 payloadHash = keccak256(payload);
        
        if (!gateway.validateContractCall(commandId, sourceChain, sourceAddress, payloadHash)) {
            revert NotApprovedByGateway();
        }
        
        _execute(sourceChain, sourceAddress, payload);
    }

    /**
     * @dev Execute a cross-chain contract call with token transfer
     * @param commandId Unique identifier for the command
     * @param sourceChain Name of the source chain
     * @param sourceAddress Address of the sender on the source chain
     * @param payload The payload sent from the source chain
     * @param tokenSymbol Symbol of the token transferred
     * @param amount Amount of tokens transferred
     */
    function executeWithToken(
        bytes32 commandId,
        string calldata sourceChain,
        string calldata sourceAddress,
        bytes calldata payload,
        string calldata tokenSymbol,
        uint256 amount
    ) external override onlyGateway {
        bytes32 payloadHash = keccak256(payload);
        
        if (!gateway.validateContractCallAndMint(
            commandId, 
            sourceChain, 
            sourceAddress, 
            payloadHash, 
            tokenSymbol, 
            amount
        )) {
            revert NotApprovedByGateway();
        }
        
        _executeWithToken(sourceChain, sourceAddress, payload, tokenSymbol, amount);
    }

    /**
     * @dev Override this function to implement your application logic
     * @param sourceChain Name of the source chain
     * @param sourceAddress Address of the sender on the source chain
     * @param payload The payload sent from the source chain
     */
    function _execute(
        string calldata sourceChain,
        string calldata sourceAddress,
        bytes calldata payload
    ) internal virtual {}

    /**
     * @dev Override this function to implement your application logic with tokens
     * @param sourceChain Name of the source chain
     * @param sourceAddress Address of the sender on the source chain
     * @param payload The payload sent from the source chain
     * @param tokenSymbol Symbol of the token transferred
     * @param amount Amount of tokens transferred
     */
    function _executeWithToken(
        string calldata sourceChain,
        string calldata sourceAddress,
        bytes calldata payload,
        string calldata tokenSymbol,
        uint256 amount
    ) internal virtual {}

    /**
     * @dev Convenience function to call another contract on another chain
     * @param destinationChain Name of the destination chain
     * @param destinationAddress Address of the destination contract
     * @param payload Payload to send
     */
    function _callContract(
        string memory destinationChain,
        string memory destinationAddress,
        bytes memory payload
    ) internal {
        gateway.callContract(destinationChain, destinationAddress, payload);
    }

    /**
     * @dev Convenience function to call another contract on another chain with tokens
     * @param destinationChain Name of the destination chain
     * @param destinationAddress Address of the destination contract
     * @param payload Payload to send
     * @param symbol Token symbol
     * @param amount Token amount
     */
    function _callContractWithToken(
        string memory destinationChain,
        string memory destinationAddress,
        bytes memory payload,
        string memory symbol,
        uint256 amount
    ) internal {
        gateway.callContractWithToken(destinationChain, destinationAddress, payload, symbol, amount);
    }
}

contracts/IU2UGateway.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {EnumerableSet} from "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import {ECDSA} from "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import {SignatureChecker} from "@openzeppelin/contracts/utils/cryptography/SignatureChecker.sol";
import "./interfaces/IIU2UGateway.sol";
import "./IU2UExecutable.sol";
import "./IU2U.sol";

contract IU2UGateway is IIU2UGateway, Ownable {
    using EnumerableSet for EnumerableSet.AddressSet;
    using SignatureChecker for address;
    using ECDSA for bytes32;

    uint256 u2u_chainid = 2484; // 2484 is U2U Nebulas Testnet
    IU2U public immutable iu2uToken;
    EnumerableSet.AddressSet private relayers;

    mapping(address => bool) public whitelisted;
    mapping(bytes32 => bool) public commandExecuted;
    mapping(string => uint256) public chainIds;
    mapping(uint256 => string) public chainNames;
    mapping(bytes32 => bytes) public approvedPayloads; // Store approved payloads for execution

    // GMP Protocol structures
    struct Command {
        uint256 commandType;
        bytes data;
    }

    // Command types
    uint256 public constant COMMAND_APPROVE_CONTRACT_CALL = 0;
    uint256 public constant COMMAND_APPROVE_CONTRACT_CALL_WITH_MINT = 1;
    uint256 public constant COMMAND_BURN_TOKEN = 2;
    uint256 public constant COMMAND_MINT_TOKEN = 4;

    event RelayerAdded(address indexed relayer);
    event RelayerRemoved(address indexed relayer);
    event ChainAdded(string indexed chainName, uint256 chainId);
    event ChainRemoved(string indexed chainName);

    constructor(address owner_, address payable iu2uToken_) Ownable() {
        // Transfer ownership to the specified owner
        _transferOwnership(owner_);

        // Set IU2U token address
        iu2uToken = IU2U(iu2uToken_);

        // Initialize default chains
        chainIds["u2u-nebulas-testnet"] = 2484;
        chainNames[2484] = "u2u-nebulas-testnet";
        chainIds["ethereum"] = 1;
        chainNames[1] = "ethereum";
        chainIds["bsc"] = 56;
        chainNames[56] = "bsc";
        chainIds["polygon"] = 137;
        chainNames[137] = "polygon";
        chainIds["base"] = 8453;
        chainNames[8453] = "base";
        chainIds["arbitrum"] = 42161;
        chainNames[42161] = "arbitrum";
        chainIds["avalanche"] = 43114;
        chainNames[43114] = "avalanche";
        chainIds["optimism"] = 10;
        chainNames[10] = "optimism";
    }

    modifier onlyRelayer() {
        require(whitelisted[msg.sender], "Caller not whitelisted relayers");
        _;
    }

    modifier onlyU2UChain() {
        require(block.chainid == u2u_chainid, "Not U2U Chain");
        _;
    }

    modifier notExecuted(bytes32 commandId) {
        require(!commandExecuted[commandId], "Command already executed");
        _;
    }

    // Chain management functions
    function addChain(string memory chainName, uint256 chainId) external onlyOwner {
        require(bytes(chainName).length > 0, "Invalid chain name");
        require(chainId > 0, "Invalid chain ID");
        require(chainIds[chainName] == 0, "Chain already exists");

        chainIds[chainName] = chainId;
        chainNames[chainId] = chainName;

        emit ChainAdded(chainName, chainId);
    }

    function removeChain(string memory chainName) external onlyOwner {
        uint256 chainId = chainIds[chainName];
        require(chainId > 0, "Chain does not exist");

        delete chainIds[chainName];
        delete chainNames[chainId];

        emit ChainRemoved(chainName);
    }

    function getChainId(string memory chainName) external view returns (uint256) {
        return chainIds[chainName];
    }

    function getChainName(uint256 chainId) external view returns (string memory) {
        return chainNames[chainId];
    }

    // add new whitelisted relayer
    function addWhitelistedRelayer(address relayer) public onlyOwner {
        require(relayer != address(0), 'address is not valid');
        require(!whitelisted[relayer], 'Already relayer');

        whitelisted[relayer] = true;
        relayers.add(relayer);

        emit RelayerAdded(relayer);
    }

    // remove current whitelisted relayer
    function removeWhitelistedRelayer(address relayer) public onlyOwner {
        require(whitelisted[relayer], 'Relayer not whitelisted');

        whitelisted[relayer] = false;
        relayers.remove(relayer);

        emit RelayerRemoved(relayer);
    }

    // GMP Protocol Core Functions
    /**
     * @dev Call a contract on another chain
     * @param destinationChain The name of the destination chain
     * @param destinationContractAddress The address of the contract to call
     * @param payload The payload to send to the contract
     */
    function callContract(
        string memory destinationChain,
        string memory destinationContractAddress,
        bytes memory payload
    ) external {
        require(chainIds[destinationChain] > 0, "Unsupported destination chain");
        require(bytes(destinationContractAddress).length > 0, "Invalid destination address");

        bytes32 payloadHash = keccak256(payload);

        emit ContractCall(
            msg.sender,
            destinationChain,
            destinationContractAddress,
            payloadHash,
            payload
        );
    }

    /**
     * @dev Call a contract on another chain with token transfer
     * @param destinationChain The name of the destination chain
     * @param destinationContractAddress The address of the contract to call
     * @param payload The payload to send to the contract
     * @param symbol The symbol of the token to send
     * @param amount The amount of tokens to send
     */
    function callContractWithToken(
        string memory destinationChain,
        string memory destinationContractAddress,
        bytes memory payload,
        string memory symbol,
        uint256 amount
    ) external {
        require(chainIds[destinationChain] > 0, "Unsupported destination chain");
        require(bytes(destinationContractAddress).length > 0, "Invalid destination address");
        require(amount > 0, "Amount must be greater than zero");
        require(keccak256(bytes(symbol)) == keccak256(bytes("IU2U")), "Unsupported token");

        // Burn tokens from sender on source chain
        iu2uToken.burn(msg.sender, amount);

        bytes32 payloadHash = keccak256(payload);

        emit ContractCallWithToken(
            msg.sender,
            destinationChain,
            destinationContractAddress,
            payloadHash,
            payload,
            symbol,
            amount
        );
    }

    /**
     * @dev Send tokens to another chain
     * @param destinationChain The name of the destination chain
     * @param destinationAddress The address to send tokens to
     * @param symbol The symbol of the token to send
     * @param amount The amount of tokens to send
     */
    function sendToken(
        string memory destinationChain,
        string memory destinationAddress,
        string memory symbol,
        uint256 amount
    ) external {
        require(chainIds[destinationChain] > 0, "Unsupported destination chain");
        require(bytes(destinationAddress).length > 0, "Invalid destination address");
        require(amount > 0, "Amount must be greater than zero");
        require(keccak256(bytes(symbol)) == keccak256(bytes("IU2U")), "Unsupported token");

        // Burn tokens from sender on source chain
        iu2uToken.burn(msg.sender, amount);

        emit TokenSent(
            msg.sender,
            destinationChain,
            destinationAddress,
            symbol,
            amount
        );
    }

    function sendToken(
        string memory destinationChain,
        string memory destinationAddress,        
        string memory symbol
    ) external payable onlyU2UChain {
        uint256 amount = msg.value;
        require(amount > 0, "Amount must be greater than zero");
        require(keccak256(bytes(symbol)) == keccak256(bytes("IU2U")), "Unsupported token");

        payable(address(iu2uToken)).call{value: amount};

        emit TokenSent(
            msg.sender,
            destinationChain,
            destinationAddress,
            symbol,
            amount
        );
    }

    // GMP Command Execution Functions (for relayers)

    /**
     * @dev Execute commands from relayers
     * @param commandId Unique identifier for the command
     * @param commands Array of commands to execute
     * @param signature Signature from authorized relayer
     */
    function execute(
        bytes32 commandId,
        Command[] memory commands,
        bytes memory signature
    ) external onlyRelayer notExecuted(commandId) {
        // Mark command as executed to prevent replay
        commandExecuted[commandId] = true;

        // Verify signature
        bytes32 hash = keccak256(abi.encode(commandId, commands));
        address signer = recoverSigner(hash, signature);
        require(whitelisted[signer], "Invalid signer");

        // Execute all commands
        for (uint256 i = 0; i < commands.length; i++) {
            _executeCommand(commandId, commands[i]);
        }

        emit Executed(commandId);
    }

    /**
     * @dev Internal function to execute individual commands
     */
    function _executeCommand(bytes32 commandId, Command memory command) internal {
        if (command.commandType == COMMAND_APPROVE_CONTRACT_CALL) {
            _approveContractCall(commandId, command.data);
        } else if (command.commandType == COMMAND_APPROVE_CONTRACT_CALL_WITH_MINT) {
            _approveContractCallWithMint(commandId, command.data);
        } else if (command.commandType == COMMAND_MINT_TOKEN) {
            _mintToken(command.data);
        } else if (command.commandType == COMMAND_BURN_TOKEN) {
            _burnToken(command.data);
        } else {
            revert("Unknown command type");
        }
    }

    /**
     * @dev Approve a contract call from another chain
     */
    function _approveContractCall(bytes32 commandId, bytes memory data) internal {
        (
            string memory sourceChain,
            string memory sourceAddress,
            address contractAddress,
            bytes32 payloadHash,
            bytes32 sourceTxHash,
            uint256 sourceEventIndex,
            bytes memory payload
        ) = abi.decode(data, (string, string, address, bytes32, bytes32, uint256, bytes));

        // Verify payload hash matches
        require(keccak256(payload) == payloadHash, "Invalid payload hash");

        // Store approved payload for potential execution
        approvedPayloads[commandId] = payload;

        emit ContractCallApproved(
            commandId,
            sourceChain,
            sourceAddress,
            contractAddress,
            payloadHash,
            sourceTxHash,
            sourceEventIndex
        );

        // Execute the contract call if the contract implements IU2UExecutable
        if (contractAddress.code.length > 0) {
            try this._safeExecuteCall(
                commandId,
                sourceChain,
                sourceAddress,
                contractAddress,
                payload
            ) {
                // Call succeeded
            } catch {
                // Call failed, but we don't revert the entire transaction
                // The event is still emitted so the approval is recorded
            }
        }
    }

    /**
     * @dev Approve a contract call with mint from another chain
     */
    function _approveContractCallWithMint(bytes32 commandId, bytes memory data) internal {
        (
            string memory sourceChain,
            string memory sourceAddress,
            address contractAddress,
            bytes32 payloadHash,
            string memory symbol,
            uint256 amount,
            bytes32 sourceTxHash,
            uint256 sourceEventIndex,
            bytes memory payload
        ) = abi.decode(data, (string, string, address, bytes32, string, uint256, bytes32, uint256, bytes));

        require(keccak256(bytes(symbol)) == keccak256(bytes("IU2U")), "Unsupported token");

        // Verify payload hash matches
        require(keccak256(payload) == payloadHash, "Invalid payload hash");

        // Store approved payload for potential execution
        approvedPayloads[commandId] = payload;

        // Mint tokens to the destination contract
        iu2uToken.mint(contractAddress, amount);

        emit ContractCallApprovedWithMint(
            commandId,
            sourceChain,
            sourceAddress,
            contractAddress,
            payloadHash,
            symbol,
            amount,
            sourceTxHash,
            sourceEventIndex
        );

        // Execute the contract call with token if the contract implements IU2UExecutable
        if (contractAddress.code.length > 0) {
            try this._safeExecuteCallWithToken(
                commandId,
                sourceChain,
                sourceAddress,
                contractAddress,
                payload,
                symbol,
                amount
            ) {
                // Call succeeded
            } catch {
                // Call failed, but we don't revert the entire transaction
                // The tokens are still minted and event is emitted
            }
        }
    }

    /**
     * @dev Mint tokens (for cross-chain transfers)
     */
    function _mintToken(bytes memory data) internal {
        (address to, uint256 amount, string memory symbol) = abi.decode(data, (address, uint256, string));

        require(keccak256(bytes(symbol)) == keccak256(bytes("IU2U")), "Unsupported token");
        require(amount > 0, "Amount must be greater than zero");
        require(to != address(0), "Invalid recipient");

        // Mint tokens using the IU2U token contract
        iu2uToken.mint(to, amount);
    }

    /**
     * @dev Burn tokens (for cross-chain transfers)
     */
    function _burnToken(bytes memory data) internal {
        (address from, uint256 amount, string memory symbol) = abi.decode(data, (address, uint256, string));

        require(keccak256(bytes(symbol)) == keccak256(bytes("IU2U")), "Unsupported token");
        require(amount > 0, "Amount must be greater than zero");
        require(from != address(0), "Invalid sender");

        // Burn tokens using the IU2U token contract
        iu2uToken.burn(from, amount);
    }

    /**
     * @dev Safely execute a contract call (external function for try-catch)
     */
    function _safeExecuteCall(
        bytes32 commandId,
        string memory sourceChain,
        string memory sourceAddress,
        address contractAddress,
        bytes memory payload
    ) external {
        require(msg.sender == address(this), "Only self");

        // Try to call the execute function on the target contract
        (bool success, ) = contractAddress.call(
            abi.encodeWithSignature(
                "execute(bytes32,string,string,bytes)",
                commandId,
                sourceChain,
                sourceAddress,
                payload
            )
        );

        require(success, "Contract call failed");
    }

    /**
     * @dev Safely execute a contract call with token (external function for try-catch)
     */
    function _safeExecuteCallWithToken(
        bytes32 commandId,
        string memory sourceChain,
        string memory sourceAddress,
        address contractAddress,
        bytes memory payload,
        string memory symbol,
        uint256 amount
    ) external {
        require(msg.sender == address(this), "Only self");

        // Try to call the executeWithToken function on the target contract
        (bool success, ) = contractAddress.call(
            abi.encodeWithSignature(
                "executeWithToken(bytes32,string,string,bytes,string,uint256)",
                commandId,
                sourceChain,
                sourceAddress,
                payload,
                symbol,
                amount
            )
        );

        require(success, "Contract call with token failed");
    }

    // Utility functions

    /**
     * @dev Check if a command has been executed
     * @param commandId The command ID to check
     * @return True if the command has been executed
     */
    function isCommandExecuted(bytes32 commandId) external view returns (bool) {
        return commandExecuted[commandId];
    }

    /**
     * @dev Check if a contract call is approved
     * @param commandId Unique identifier for the command
     * @return True if the contract call is approved
     */
    function isContractCallApproved(
        bytes32 commandId,
        string calldata /* sourceChain */,
        string calldata /* sourceAddress */,
        address /* contractAddress */,
        bytes32 /* payloadHash */
    ) external view returns (bool) {
        return commandExecuted[commandId];
    }

    /**
     * @dev Check if a contract call with mint is approved
     * @param commandId Unique identifier for the command
     * @return True if the contract call with mint is approved
     */
    function isContractCallAndMintApproved(
        bytes32 commandId,
        string calldata /* sourceChain */,
        string calldata /* sourceAddress */,
        address /* contractAddress */,
        bytes32 /* payloadHash */,
        string calldata /* symbol */,
        uint256 /* amount */
    ) external view returns (bool) {
        return commandExecuted[commandId];
    }

    /**
     * @dev Get approved payload for a command
     * @param commandId The command ID to get payload for
     * @return The approved payload data
     */
    function getApprovedPayload(bytes32 commandId) external view returns (bytes memory) {
        return approvedPayloads[commandId];
    }

    /**
     * @dev Validate contract call for IU2UExecutable contracts
     * @param commandId Unique identifier for the command
     * @param sourceChain Name of the source chain
     * @param sourceAddress Address of the sender on the source chain
     * @param payloadHash Hash of the payload
     * @return True if valid and approved
     */
    function validateContractCall(
        bytes32 commandId,
        string calldata sourceChain,
        string calldata sourceAddress,
        bytes32 payloadHash
    ) external view returns (bool) {
        if (!commandExecuted[commandId]) {
            return false;
        }

        bytes memory storedPayload = approvedPayloads[commandId];
        if (storedPayload.length == 0) {
            return false;
        }

        return keccak256(storedPayload) == payloadHash;
    }

    /**
     * @dev Validate contract call with mint for IU2UExecutable contracts
     * @param commandId Unique identifier for the command
     * @param sourceChain Name of the source chain
     * @param sourceAddress Address of the sender on the source chain
     * @param payloadHash Hash of the payload
     * @param symbol Token symbol
     * @param amount Token amount
     * @return True if valid and approved
     */
    function validateContractCallAndMint(
        bytes32 commandId,
        string calldata sourceChain,
        string calldata sourceAddress,
        bytes32 payloadHash,
        string calldata symbol,
        uint256 amount
    ) external view returns (bool) {
        if (!commandExecuted[commandId]) {
            return false;
        }

        bytes memory storedPayload = approvedPayloads[commandId];
        if (storedPayload.length == 0) {
            return false;
        }

        return keccak256(storedPayload) == payloadHash;
    }

    /**
     * @dev Check if an address is a whitelisted relayer
     * @param relayer The address to check
     * @return True if the address is whitelisted
     */
    function isWhitelistedRelayer(address relayer) external view returns (bool) {
        return whitelisted[relayer];
    }

    /**
     * @dev Get all whitelisted relayers
     * @return Array of whitelisted relayer addresses
     */
    function getAllRelayers() external view returns (address[] memory) {
        uint256 length = relayers.length();
        address[] memory result = new address[](length);

        for (uint256 i = 0; i < length; i++) {
            result[i] = relayers.at(i);
        }

        return result;
    }

    /**
     * @dev Get the number of whitelisted relayers
     * @return The number of relayers
     */
    function getRelayerCount() external view returns (uint256) {
        return relayers.length();
    }

    // Recover the signer address from the signature
    function recoverSigner(bytes32 _hash, bytes memory _signature) public pure returns (address) {
        bytes32 ethSignedMessageHash = _hash.toEthSignedMessageHash();
        return ethSignedMessageHash.recover(_signature);
    }

}

contracts/libraries/CalldataLibrary.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

/**
 * @title CalldataLibrary  
 * @notice Library for generating calldata for different DEX routers
 */
library CalldataLibrary {

    /**
     * @notice Generate Uniswap V2 style swap calldata
     */
    function generateUniswapV2Calldata(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) internal pure returns (bytes memory) {
        return abi.encodeWithSignature(
            "swapExactTokensForTokens(uint256,uint256,address[],address,uint256)",
            amountIn,
            amountOutMin,
            path,
            to,
            deadline
        );
    }

    /**
     * @notice Generate Uniswap V3 exact input swap calldata
     */
    function generateUniswapV3ExactInputCalldata(
        uint256 amountIn,
        uint256 amountOutMin,
        bytes calldata path,
        address recipient,
        uint256 deadline
    ) internal pure returns (bytes memory) {
        bytes memory params = abi.encode(
            path,
            recipient,
            deadline,
            amountIn,
            amountOutMin
        );
        
        return abi.encodeWithSignature(
            "exactInput((bytes,address,uint256,uint256,uint256))",
            params
        );
    }

    /**
     * @notice Generate Curve swap calldata (simplified implementation)
     */
    function generateCurveSwapCalldata(
        address,
        address,
        uint256 dx,
        uint256 minDy
    ) internal pure returns (bytes memory) {
        return abi.encodeWithSignature(
            "exchange(int128,int128,uint256,uint256)",
            int128(0),
            int128(1),  
            dx,
            minDy
        );
    }

    /**
     * @notice Generate 1inch style aggregated swap calldata
     */
    function generate1inchCalldata(
        address tokenIn,
        address tokenOut,
        uint256 amount,
        uint256 minReturn,
        bytes calldata data
    ) internal pure returns (bytes memory) {
        return abi.encodeWithSignature(
            "swap(address,address,uint256,uint256,bytes)",
            tokenIn,
            tokenOut,
            amount,
            minReturn,
            data
        );
    }

    /**
     * @notice Generate ParaSwap V5 swap calldata
     */
    function generateParaSwapCalldata(
        address fromToken,
        uint256 fromAmount,
        uint256 toAmount,
        uint256 expectedAmount,
        address beneficiary,
        bytes calldata swapData
    ) internal pure returns (bytes memory) {
        return abi.encodeWithSignature(
            "multiSwap((address,uint256,uint256,uint256,address,string,bool,bytes))",
            fromToken,
            fromAmount,
            toAmount,
            expectedAmount,
            beneficiary,
            "", // referrer
            false, // useReduxToken
            swapData
        );
    }

    /**
     * @notice Generate 0x Protocol swap calldata
     */
    function generateZeroXCalldata(
        bytes32 orderHash,
        bytes calldata signature,
        uint128 takerTokenFillAmount
    ) internal pure returns (bytes memory) {
        return abi.encodeWithSignature(
            "fillLimitOrder(bytes32,bytes,uint128)",
            orderHash,
            signature,
            takerTokenFillAmount
        );
    }

    /**
     * @notice Generate Kyber Network swap calldata
     */
    function generateKyberCalldata(
        address src,
        uint256 srcAmount,
        address dest,
        uint256 minConversionRate
    ) internal pure returns (bytes memory) {
        return abi.encodeWithSignature(
            "swapTokenToToken(address,uint256,address,uint256)",
            src,
            srcAmount,
            dest,
            minConversionRate
        );
    }

    /**
     * @notice Generate DODO swap calldata
     */
    function generateDODOCalldata(
        address fromToken,
        address toToken,
        uint256 fromTokenAmount,
        uint256 minReturnAmount,
        address[] calldata dodoPairs,
        uint256 directions,
        bool isIncentive,
        uint256 deadLine
    ) internal pure returns (bytes memory) {
        return abi.encodeWithSignature(
            "dodoSwapV1(address,address,uint256,uint256,address[],uint256,bool,uint256)",
            fromToken,
            toToken,
            fromTokenAmount,
            minReturnAmount,
            dodoPairs,
            directions,
            isIncentive,
            deadLine
        );
    }

    /**
     * @notice Generate Bancor swap calldata
     */
    function generateBancorCalldata(
        address[] calldata path,
        uint256 amount,
        uint256 minReturn,
        address beneficiary,
        address affiliateAccount,
        uint256 affiliateFee
    ) internal pure returns (bytes memory) {
        return abi.encodeWithSignature(
            "convertByPath(address[],uint256,uint256,address,address,uint256)",
            path,
            amount,
            minReturn,
            beneficiary,
            affiliateAccount,
            affiliateFee
        );
    }
}

contracts/libraries/MulticallLibraryV2.s.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "./QuoteLibrary.sol";

// Multicall3 interface
interface IMulticall3 {
    struct Call3 {
        address target;
        bool allowFailure;
        bytes callData;
    }
}

/**
 * @title MulticallLibrary
 * @notice Library for handling multicall operations and optimal router selection
 */
library MulticallLibrary {

    /**
     * @notice Get optimal router using individual calls
     */
    function getOptimalRouter(
        uint256 chainId,
        address tokenIn,
        address tokenOut,
        uint256 amount,
        mapping(uint256 => bool) storage supportedChains,
        mapping(uint256 => mapping(QuoteLibrary.RouterType => QuoteLibrary.RouterConfig)) storage routers,
        mapping(uint256 => address) storage curveRegistries,
        mapping(uint256 => address) storage balancerVaults,
        mapping(uint256 => mapping(bytes32 => QuoteLibrary.PoolConfig)) storage poolConfigs
    ) internal returns (address, QuoteLibrary.RouterType, uint256) {
        require(supportedChains[chainId], "Chain not supported");
        require(amount > 0, "Amount must be greater than 0");

        uint256 bestOutput = 0;
        address bestRouter = address(0);
        QuoteLibrary.RouterType bestType;
        bool routerFound = false;

        for (uint256 i = 0; i < 37; i++) {
            (bool valid, uint256 output, address routerAddr) = _evaluateRouter(
                chainId,
                QuoteLibrary.RouterType(i),
                tokenIn,
                tokenOut,
                amount,
                routers,
                curveRegistries,
                balancerVaults,
                poolConfigs
            );

            if (valid && output > bestOutput) {
                bestOutput = output;
                bestRouter = routerAddr;
                bestType = QuoteLibrary.RouterType(i);
                routerFound = true;
            }
        }

        require(routerFound, "No successful quotes found");
        return (bestRouter, bestType, bestOutput);
    }

    function _evaluateRouter(
        uint256 chainId,
        QuoteLibrary.RouterType rt,
        address tokenIn,
        address tokenOut,
        uint256 amount,
        mapping(uint256 => mapping(QuoteLibrary.RouterType => QuoteLibrary.RouterConfig)) storage routers,
        mapping(uint256 => address) storage curveRegistries,
        mapping(uint256 => address) storage balancerVaults,
        mapping(uint256 => mapping(bytes32 => QuoteLibrary.PoolConfig)) storage poolConfigs
    ) private returns (bool, uint256, address) {
        QuoteLibrary.RouterConfig memory config = routers[chainId][rt];

        if (!config.isActive || config.routerAddress == address(0)) {
            return (false, 0, address(0));
        }

        uint256 output = QuoteLibrary.getExpectedOutput(
            chainId,
            rt,
            tokenIn,
            tokenOut,
            amount,
            routers,
            curveRegistries,
            balancerVaults,
            poolConfigs
        );

        return (true, output, config.routerAddress);
    }

    /**
     * @notice Prepare multicall data for all active routers
     */
    function prepareMulticallQuotes(
        uint256 chainId,
        address tokenIn,
        address tokenOut,
        uint256 amount,
        mapping(uint256 => bool) storage supportedChains,
        mapping(uint256 => mapping(QuoteLibrary.RouterType => QuoteLibrary.RouterConfig)) storage routers
    ) internal view returns (IMulticall3.Call3[] memory calls) {
        require(supportedChains[chainId], "Chain not supported");
        require(amount > 0, "Amount must be greater than 0");
        
        uint256 activeCount = 0;
        // Count active routers from all 37 router types
        for (uint256 i = 0; i < 37; i++) {
            QuoteLibrary.RouterType rt = QuoteLibrary.RouterType(i);
            QuoteLibrary.RouterConfig memory config = routers[chainId][rt];
            if (config.isActive && config.routerAddress != address(0)) {
                activeCount++;
            }
        }
        
        calls = new IMulticall3.Call3[](activeCount);
        uint256 callIndex = 0;
        
        // Generate calls for all active routers
        for (uint256 i = 0; i < 37; i++) {
            QuoteLibrary.RouterType rt = QuoteLibrary.RouterType(i);
            QuoteLibrary.RouterConfig memory config = routers[chainId][rt];
            
            if (config.isActive && config.routerAddress != address(0)) {
                calls[callIndex] = IMulticall3.Call3({
                    target: config.routerAddress,
                    allowFailure: true,
                    callData: _generateQuoteCalldata(rt, tokenIn, tokenOut, amount)
                });
                callIndex++;
            }
        }
        
        return calls;
    }

    /**
     * @notice Generate calldata for price quotes based on router type
     */
    function _generateQuoteCalldata(
        QuoteLibrary.RouterType routerType,
        address tokenIn,
        address tokenOut,
        uint256 amount
    ) private pure returns (bytes memory) {
        // Uniswap V2 style routers (AMM with constant product)
        if (routerType == QuoteLibrary.RouterType.UNISWAP_V2 || 
            routerType == QuoteLibrary.RouterType.SUSHISWAP || 
            routerType == QuoteLibrary.RouterType.PANCAKESWAP || 
            routerType == QuoteLibrary.RouterType.QUICKSWAP ||
            routerType == QuoteLibrary.RouterType.SHIBASWAP ||
            routerType == QuoteLibrary.RouterType.TRADERJOE ||
            routerType == QuoteLibrary.RouterType.SPOOKYSWAP ||
            routerType == QuoteLibrary.RouterType.SPIRITSWAP ||
            routerType == QuoteLibrary.RouterType.APESWAP ||
            routerType == QuoteLibrary.RouterType.BISWAP ||
            routerType == QuoteLibrary.RouterType.MDEX ||
            routerType == QuoteLibrary.RouterType.CAMELOT ||
            routerType == QuoteLibrary.RouterType.ZYBERSWAP) {
            
            address[] memory path = new address[](2);
            path[0] = tokenIn;
            path[1] = tokenOut;
            
            return abi.encodeWithSignature(
                "getAmountsOut(uint256,address[])",
                amount,
                path
            );
            
        // Uniswap V3 style routers (concentrated liquidity)
        } else if (routerType == QuoteLibrary.RouterType.UNISWAP_V3 ||
                   routerType == QuoteLibrary.RouterType.SUSHISWAP_V3 ||
                   routerType == QuoteLibrary.RouterType.PANCAKESWAP_V3 ||
                   routerType == QuoteLibrary.RouterType.RAMSES ||
                   routerType == QuoteLibrary.RouterType.ALGEBRA) {
            return abi.encodeWithSignature(
                "quoteExactInputSingle(address,address,uint24,uint256,uint160)",
                tokenIn,
                tokenOut,
                3000, // 0.3% fee tier
                amount,
                0
            );
            
        // Solidly style routers (Velodrome, Aerodrome, etc.)
        } else if (routerType == QuoteLibrary.RouterType.VELODROME ||
                   routerType == QuoteLibrary.RouterType.AERODROME ||
                   routerType == QuoteLibrary.RouterType.SOLIDLY ||
                   routerType == QuoteLibrary.RouterType.THENA ||
                   routerType == QuoteLibrary.RouterType.CHRONOS) {
            // Solidly-style routes with stable flag
            return abi.encodeWithSignature(
                "getAmountsOut(uint256,(address,address,bool)[])",
                amount,
                _getSolidlyRoute(tokenIn, tokenOut)
            );
            
        // Curve pools
        } else if (routerType == QuoteLibrary.RouterType.CURVE) {
            return abi.encodeWithSignature(
                "get_dy(int128,int128,uint256)",
                0, // token in index
                1, // token out index  
                amount
            );
            
        // Stableswap protocols (Platypus, Wombat)
        } else if (routerType == QuoteLibrary.RouterType.PLATYPUS ||
                   routerType == QuoteLibrary.RouterType.WOMBAT) {
            return abi.encodeWithSignature(
                "quotePotentialSwap(address,address,uint256)",
                tokenIn,
                tokenOut,
                amount
            );
            
        // GMX Router
        } else if (routerType == QuoteLibrary.RouterType.GMXSWAP) {
            return abi.encodeWithSignature(
                "getAmountOut(address,address,uint256)",
                tokenIn,
                tokenOut,
                amount
            );
            
        } else {
            // For unsupported or complex routers, return empty calldata
            return "";
        }
    }
    
    /**
     * @notice Helper function to create Solidly-style route
     */
    function _getSolidlyRoute(address tokenIn, address tokenOut) 
        private 
        pure 
        returns (bytes memory) {
        // Create a single route struct (from, to, stable)
        // For simplicity, we assume non-stable pairs
        return abi.encode(tokenIn, tokenOut, false);
    }
}

contracts/libraries/QuoteLibrary.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

// Router interfaces for price quotes
interface IUniswapV2Router {
    function getAmountsOut(uint amountIn, address[] calldata path)
        external view returns (uint[] memory amounts);
}

interface IUniswapV3Quoter {
    function quoteExactInputSingle(
        address tokenIn,
        address tokenOut,
        uint24 fee,
        uint256 amountIn,
        uint160 sqrtPriceLimitX96
    ) external returns (uint256 amountOut);
}

// Curve pool interfaces
interface ICurvePool {
    function get_dy(int128 i, int128 j, uint256 dx) external view returns (uint256);
    function coins(uint256 i) external view returns (address);
    function exchange(int128 i, int128 j, uint256 dx, uint256 min_dy) external returns (uint256);
}

interface ICurveRegistry {
    function find_pool_for_coins(address from, address to) external view returns (address);
    function get_coin_indices(address pool, address from, address to) external view returns (int128, int128, bool);
}

// Balancer interfaces
interface IBalancerVault {
    struct SingleSwap {
        bytes32 poolId;
        uint8 kind; // 0 = GIVEN_IN, 1 = GIVEN_OUT
        address assetIn;
        address assetOut;
        uint256 amount;
        bytes userData;
    }

    struct FundManagement {
        address sender;
        bool fromInternalBalance;
        address payable recipient;
        bool toInternalBalance;
    }

    function queryBatchSwap(
        uint8 kind,
        BatchSwapStep[] memory swaps,
        address[] memory assets,
        FundManagement memory funds
    ) external returns (int256[] memory);

    function batchSwap(
        uint8 kind,
        BatchSwapStep[] memory swaps,
        address[] memory assets,
        FundManagement memory funds,
        int256[] memory limits,
        uint256 deadline
    ) external returns (int256[] memory);

    struct BatchSwapStep {
        bytes32 poolId;
        uint256 assetInIndex;
        uint256 assetOutIndex;
        uint256 amount;
        bytes userData;
    }
}

// 1inch interfaces
interface IOneInchV5Router {
    function getExpectedReturn(
        address fromToken,
        address destToken,
        uint256 amount,
        uint256 parts,
        uint256 flags
    ) external view returns (uint256 returnAmount, uint256[] memory distribution);
    
    function swap(
        address fromToken,
        address destToken,
        uint256 amount,
        uint256 minReturn,
        bytes calldata data
    ) external returns (uint256);
}

// ParaSwap interfaces
interface IParaSwapV5 {
    struct SellData {
        address fromToken;
        uint256 fromAmount;
        uint256 toAmount;
        uint256 expectedAmount;
        address beneficiary;
        string referrer;
        bool useReduxToken;
        bytes data;
    }
    
    function multiSwap(SellData calldata data) external payable returns (uint256);
    
    function getRate(
        address srcToken,
        address destToken,
        uint256 srcAmount
    ) external view returns (uint256);
}

// 0x Protocol interfaces
interface IZeroXV4 {
    function fillLimitOrder(
        bytes32 orderHash,
        bytes calldata signature,
        uint128 takerTokenFillAmount
    ) external payable returns (uint128 takerTokenFilledAmount, uint128 makerTokenFilledAmount);
}

// Kyber Network interfaces
interface IKyberNetworkProxy {
    function getExpectedRate(
        address src,
        address dest,
        uint256 srcQty
    ) external view returns (uint256 expectedRate, uint256 slippageRate);
    
    function swapTokenToToken(
        address src,
        uint256 srcAmount,
        address dest,
        uint256 minConversionRate
    ) external returns (uint256);
}

// DODO interfaces
interface IDODOProxy {
    function dodoSwapV1(
        address fromToken,
        address toToken,
        uint256 fromTokenAmount,
        uint256 minReturnAmount,
        address[] memory dodoPairs,
        uint256 directions,
        bool isIncentive,
        uint256 deadLine
    ) external payable returns (uint256 returnAmount);
    
    function querySellQuoteTokenForBaseToken(
        address dodoPool,
        uint256 amount
    ) external view returns (uint256);
}

// Bancor interfaces
interface IBancorNetwork {
    function convertByPath(
        address[] memory path,
        uint256 amount,
        uint256 minReturn,
        address beneficiary,
        address affiliateAccount,
        uint256 affiliateFee
    ) external payable returns (uint256);
    
    function rateByPath(
        address[] memory path,
        uint256 amount
    ) external view returns (uint256);
}

// Velodrome/Aerodrome interfaces (Solidly forks)
interface IVelodromeRouter {
    struct Route {
        address from;
        address to;
        bool stable;
    }
    
    function getAmountsOut(uint amountIn, Route[] memory routes)
        external view returns (uint[] memory amounts);
        
    function swapExactTokensForTokens(
        uint amountIn,
        uint amountOutMin,
        Route[] calldata routes,
        address to,
        uint deadline
    ) external returns (uint[] memory amounts);
}

// Ramses interfaces (Uniswap V3 fork with concentrated liquidity)
interface IRamsesV3Quoter {
    function quoteExactInputSingle(
        address tokenIn,
        address tokenOut,
        uint24 fee,
        uint256 amountIn,
        uint160 sqrtPriceLimitX96
    ) external returns (uint256 amountOut);
}

// Solidly interfaces
interface ISolidlyRouter {
    struct Route {
        address from;
        address to;
        bool stable;
    }
    
    function getAmountsOut(uint amountIn, Route[] memory routes)
        external view returns (uint[] memory amounts);
}

// Thena interfaces (BSC)
interface IThenaRouter {
    struct Route {
        address from;
        address to;
        bool stable;
    }
    
    function getAmountsOut(uint amountIn, Route[] memory routes)
        external view returns (uint[] memory amounts);
}

// Camelot interfaces (Arbitrum)
interface ICamelotRouter {
    function getAmountsOut(uint amountIn, address[] calldata path)
        external view returns (uint[] memory amounts);
}

// Chronos interfaces
interface IChronosRouter {
    struct Route {
        address from;
        address to;
        bool stable;
    }
    
    function getAmountsOut(uint amountIn, Route[] memory routes)
        external view returns (uint[] memory amounts);
}

// ZyberSwap interfaces (Arbitrum)
interface IZyberSwapRouter {
    function getAmountsOut(uint amountIn, address[] calldata path)
        external view returns (uint[] memory amounts);
}

// Beethoven X interfaces (Fantom/Optimism - Balancer fork)
interface IBeethovenXVault {
    struct SingleSwap {
        bytes32 poolId;
        uint8 kind;
        address assetIn;
        address assetOut;
        uint256 amount;
        bytes userData;
    }

    struct FundManagement {
        address sender;
        bool fromInternalBalance;
        address payable recipient;
        bool toInternalBalance;
    }

    function queryBatchSwap(
        uint8 kind,
        IBalancerVault.BatchSwapStep[] memory swaps,
        address[] memory assets,
        FundManagement memory funds
    ) external returns (int256[] memory);
}

// Platypus interfaces (Avalanche stableswap)
interface IPlatypusPool {
    function quotePotentialSwap(
        address fromToken,
        address toToken,
        uint256 fromAmount
    ) external view returns (uint256 potentialOutcome, uint256 haircut);
    
    function swap(
        address fromToken,
        address toToken,
        uint256 fromAmount,
        uint256 minimumToAmount,
        address to,
        uint256 deadline
    ) external returns (uint256 actualToAmount, uint256 haircut);
}

// Wombat interfaces (Multi-chain stableswap)
interface IWombatPool {
    function quotePotentialSwap(
        address fromToken,
        address toToken,
        int256 fromAmount
    ) external view returns (uint256 potentialOutcome, uint256 haircut);
    
    function swap(
        address fromToken,
        address toToken,
        uint256 fromAmount,
        uint256 minimumToAmount,
        address to,
        uint256 deadline
    ) external returns (uint256 actualToAmount, uint256 haircut);
}

// GMX Swap interfaces
interface IGMXRouter {
    function swap(
        address[] memory path,
        uint256 amountIn,
        uint256 minOut,
        address receiver
    ) external;
    
    function getAmountOut(
        address tokenIn,
        address tokenOut,
        uint256 amountIn
    ) external view returns (uint256 amountOut, uint256 feeAmount);
}

// Maverick interfaces (concentrated liquidity)
interface IMaverickPool {
    function calculateSwap(
        address recipient,
        bool tokenAIn,
        int256 amountSpecified,
        uint256 sqrtPriceLimitD18
    ) external view returns (int256 amountIn, int256 amountOut);
}

// Algebra interfaces (Uniswap V3 fork)
interface IAlgebraQuoter {
    function quoteExactInputSingle(
        address tokenIn,
        address tokenOut,
        uint256 amountIn,
        uint160 limitSqrtPrice
    ) external returns (uint256 amountOut);
}

/**
 * @title QuoteLibrary
 * @notice Library for getting price quotes from different DEX protocols
 */
library QuoteLibrary {
    
    // Router types - Extended with more famous DEXes
    enum RouterType {
        UNISWAP_V2,
        UNISWAP_V3,
        SUSHISWAP,
        SUSHISWAP_V3,
        PANCAKESWAP,
        PANCAKESWAP_V3,
        QUICKSWAP,
        CURVE,
        BALANCER,
        ONE_INCH,
        PARASWAP,
        ZEROX_PROTOCOL,
        KYBER_NETWORK,
        DODO,
        BANCOR,
        SHIBASWAP,
        TRADERJOE,
        SPOOKYSWAP,
        SPIRITSWAP,
        APESWAP,
        BISWAP,
        MDEX,
        // Additional 15 DEXes
        VELODROME,
        AERODROME,
        RAMSES,
        SOLIDLY,
        THENA,
        CAMELOT,
        CHRONOS,
        ZYBERSWAP,
        BEETHOVEN_X,
        PLATYPUS,
        WOMBAT,
        GMXSWAP,
        MAVERICK,
        ALGEBRA,
        RETRO
    }

    // Router configurations
    struct RouterConfig {
        address routerAddress;
        RouterType routerType;
        bool isActive;
    }

    // Pool configurations for specific token pairs
    struct PoolConfig {
        address poolAddress;
        bytes32 poolId; // For Balancer
        bool isActive;
    }

    /**
     * @notice Get quote from Uniswap V2 style router
     */
    function getUniswapV2Quote(
        address router,
        address tokenIn,
        address tokenOut,
        uint256 amountIn
    ) internal view returns (uint256) {
        // Create path array
        address[] memory path = new address[](2);
        path[0] = tokenIn;
        path[1] = tokenOut;
        
        uint256[] memory amounts = IUniswapV2Router(router).getAmountsOut(amountIn, path);
        return amounts[1]; // Return output amount
    }

    /**
     * @notice Get quote from Uniswap V3 router
     * @dev Note: This function is not view because Uniswap V3 quoter modifies state
     */
    function getUniswapV3Quote(
        address router,
        address tokenIn,
        address tokenOut,
        uint256 amountIn
    ) internal returns (uint256) {
        // For Uniswap V3, we'd need to specify fee tier and use the quoter contract
        // This is a simplified version - in practice, you'd check multiple fee tiers
        try IUniswapV3Quoter(router).quoteExactInputSingle(
            tokenIn,
            tokenOut,
            3000, // 0.3% fee tier
            amountIn,
            0 // sqrtPriceLimitX96
        ) returns (uint256 amountOut) {
            return amountOut;
        } catch {
            // Try 0.05% fee tier
            try IUniswapV3Quoter(router).quoteExactInputSingle(
                tokenIn,
                tokenOut,
                500,
                amountIn,
                0
            ) returns (uint256 amountOut) {
                return amountOut;
            } catch {
                return 0;
            }
        }
    }

    /**
     * @notice Get quote from SushiSwap V3 (uses same interface as Uniswap V3)
     */
    function getSushiswapV3Quote(
        address router,
        address tokenIn,
        address tokenOut,
        uint256 amountIn
    ) internal returns (uint256) {
        return getUniswapV3Quote(router, tokenIn, tokenOut, amountIn);
    }

    /**
     * @notice Get quote from PancakeSwap V3 (uses same interface as Uniswap V3)
     */
    function getPancakeswapV3Quote(
        address router,
        address tokenIn,
        address tokenOut,
        uint256 amountIn
    ) internal returns (uint256) {
        return getUniswapV3Quote(router, tokenIn, tokenOut, amountIn);
    }

    /**
     * @notice Get quote from Curve pool (real implementation)
     */
    function getCurveQuote(
        uint256 chainId,
        address tokenIn,
        address tokenOut,
        uint256 amountIn,
        mapping(uint256 => address) storage curveRegistries,
        mapping(uint256 => mapping(bytes32 => PoolConfig)) storage poolConfigs
    ) internal view returns (uint256) {
        address curveRegistry = curveRegistries[chainId];
        if (curveRegistry == address(0)) {
            return 0; // No Curve registry configured
        }
        
        // Check if we have a specific pool configured for this pair
        bytes32 pairHash = keccak256(abi.encodePacked(tokenIn, tokenOut));
        PoolConfig memory poolConfig = poolConfigs[chainId][pairHash];
        
        address pool;
        if (poolConfig.isActive && poolConfig.poolAddress != address(0)) {
            pool = poolConfig.poolAddress;
        } else {
            // Try to find pool through registry
            try ICurveRegistry(curveRegistry).find_pool_for_coins(tokenIn, tokenOut) returns (address foundPool) {
                pool = foundPool;
            } catch {
                return 0; // No pool found
            }
        }
        
        if (pool == address(0)) {
            return 0;
        }
        
        // Get coin indices
        try ICurveRegistry(curveRegistry).get_coin_indices(pool, tokenIn, tokenOut) 
            returns (int128 i, int128 j, bool /* underlying */) {
            
            // Get quote from pool
            try ICurvePool(pool).get_dy(i, j, amountIn) returns (uint256 dy) {
                return dy;
            } catch {
                return 0;
            }
        } catch {
            return 0;
        }
    }

    /**
     * @notice Get quote from Balancer pool (real implementation)
     */
    function getBalancerQuote(
        uint256 chainId,
        address tokenIn,
        address tokenOut,
        uint256 amountIn,
        mapping(uint256 => address) storage balancerVaults,
        mapping(uint256 => mapping(bytes32 => PoolConfig)) storage poolConfigs
    ) internal returns (uint256) {
        address balancerVault = balancerVaults[chainId];
        if (balancerVault == address(0)) {
            return 0; // No Balancer vault configured
        }
        
        // Check if we have a specific pool configured for this pair
        bytes32 pairHash = keccak256(abi.encodePacked(tokenIn, tokenOut));
        PoolConfig memory poolConfig = poolConfigs[chainId][pairHash];
        
        if (!poolConfig.isActive || poolConfig.poolAddress == address(0) || poolConfig.poolId == bytes32(0)) {
            return 0; // No pool configured for this pair
        }
        
        // Prepare batch swap for quote
        IBalancerVault.BatchSwapStep[] memory swaps = new IBalancerVault.BatchSwapStep[](1);
        swaps[0] = IBalancerVault.BatchSwapStep({
            poolId: poolConfig.poolId,
            assetInIndex: 0,
            assetOutIndex: 1,
            amount: amountIn,
            userData: "0x"
        });
        
        address[] memory assets = new address[](2);
        assets[0] = tokenIn;
        assets[1] = tokenOut;
        
        IBalancerVault.FundManagement memory funds = IBalancerVault.FundManagement({
            sender: address(this),
            fromInternalBalance: false,
            recipient: payable(address(this)),
            toInternalBalance: false
        });
        
        try IBalancerVault(balancerVault).queryBatchSwap(0, swaps, assets, funds) 
            returns (int256[] memory deltas) {
            // Return absolute value of output delta (will be negative for output)
            return deltas.length > 1 && deltas[1] < 0 ? uint256(-deltas[1]) : 0;
        } catch {
            return 0;
        }
    }

    /**
     * @notice Get quote from 1inch aggregator
     */
    function getOneInchQuote(
        address router,
        address tokenIn,
        address tokenOut,
        uint256 amountIn
    ) internal view returns (uint256) {
        try IOneInchV5Router(router).getExpectedReturn(
            tokenIn,
            tokenOut,
            amountIn,
            10, // parts
            0   // flags
        ) returns (uint256 returnAmount, uint256[] memory) {
            return returnAmount;
        } catch {
            return 0;
        }
    }

    /**
     * @notice Get quote from ParaSwap
     */
    function getParaSwapQuote(
        address router,
        address tokenIn,
        address tokenOut,
        uint256 amountIn
    ) internal view returns (uint256) {
        try IParaSwapV5(router).getRate(
            tokenIn,
            tokenOut,
            amountIn
        ) returns (uint256 rate) {
            return rate;
        } catch {
            return 0;
        }
    }

    /**
     * @notice Get quote from Kyber Network
     */
    function getKyberQuote(
        address router,
        address tokenIn,
        address tokenOut,
        uint256 amountIn
    ) internal view returns (uint256) {
        try IKyberNetworkProxy(router).getExpectedRate(
            tokenIn,
            tokenOut,
            amountIn
        ) returns (uint256 expectedRate, uint256) {
            // Convert rate to actual amount
            return (amountIn * expectedRate) / 1e18;
        } catch {
            return 0;
        }
    }

    /**
     * @notice Get quote from DODO
     */
    function getDODOQuote(
        address router,
        address poolAddress,
        uint256 amountIn
    ) internal view returns (uint256) {
        try IDODOProxy(router).querySellQuoteTokenForBaseToken(
            poolAddress,
            amountIn
        ) returns (uint256 amount) {
            return amount;
        } catch {
            return 0;
        }
    }

    /**
     * @notice Get quote from Bancor
     */
    function getBancorQuote(
        address router,
        address[] memory path,
        uint256 amountIn
    ) internal view returns (uint256) {
        try IBancorNetwork(router).rateByPath(
            path,
            amountIn
        ) returns (uint256 amount) {
            return amount;
        } catch {
            return 0;
        }
    }
    function getExpectedOutput(
        uint256 chainId,
        RouterType routerType,
        address tokenIn,
        address tokenOut,
        uint256 amount,
        mapping(uint256 => mapping(RouterType => RouterConfig)) storage routers,
        mapping(uint256 => address) storage curveRegistries,
        mapping(uint256 => address) storage balancerVaults,
        mapping(uint256 => mapping(bytes32 => PoolConfig)) storage poolConfigs
    ) internal returns (uint256 expectedOutput) {
        RouterConfig memory config = routers[chainId][routerType];
        require(config.isActive, "Router not active");
        
        if (routerType == RouterType.UNISWAP_V2 || 
            routerType == RouterType.SUSHISWAP || 
            routerType == RouterType.PANCAKESWAP || 
            routerType == RouterType.QUICKSWAP ||
            routerType == RouterType.SHIBASWAP ||
            routerType == RouterType.TRADERJOE ||
            routerType == RouterType.SPOOKYSWAP ||
            routerType == RouterType.SPIRITSWAP ||
            routerType == RouterType.APESWAP ||
            routerType == RouterType.BISWAP ||
            routerType == RouterType.MDEX) {
            return getUniswapV2Quote(config.routerAddress, tokenIn, tokenOut, amount);
        } else if (routerType == RouterType.UNISWAP_V3) {
            return getUniswapV3Quote(config.routerAddress, tokenIn, tokenOut, amount);
        } else if (routerType == RouterType.SUSHISWAP_V3) {
            return getSushiswapV3Quote(config.routerAddress, tokenIn, tokenOut, amount);
        } else if (routerType == RouterType.PANCAKESWAP_V3) {
            return getPancakeswapV3Quote(config.routerAddress, tokenIn, tokenOut, amount);
        } else if (routerType == RouterType.CURVE) {
            return getCurveQuote(chainId, tokenIn, tokenOut, amount, curveRegistries, poolConfigs);
        } else if (routerType == RouterType.BALANCER) {
            return getBalancerQuote(chainId, tokenIn, tokenOut, amount, balancerVaults, poolConfigs);
        } else if (routerType == RouterType.ONE_INCH) {
            return getOneInchQuote(config.routerAddress, tokenIn, tokenOut, amount);
        } else if (routerType == RouterType.PARASWAP) {
            return getParaSwapQuote(config.routerAddress, tokenIn, tokenOut, amount);
        } else if (routerType == RouterType.KYBER_NETWORK) {
            return getKyberQuote(config.routerAddress, tokenIn, tokenOut, amount);
        } else if (routerType == RouterType.DODO) {
            // For DODO, we need pool address which should be configured separately
            return 0; // Placeholder - would need pool configuration
        } else if (routerType == RouterType.BANCOR) {
            // For Bancor, we need path which should be configured separately
            return 0; // Placeholder - would need path configuration
        } else if (routerType == RouterType.VELODROME) {
            return getVelodromeQuote(config, tokenIn, tokenOut, amount);
        } else if (routerType == RouterType.RAMSES) {
            return getRamsesQuote(config, tokenIn, tokenOut, amount);
        } else if (routerType == RouterType.SOLIDLY) {
            return getSolidlyQuote(config, tokenIn, tokenOut, amount);
        } else if (routerType == RouterType.THENA) {
            return getThenaQuote(config, tokenIn, tokenOut, amount);
        } else if (routerType == RouterType.CAMELOT) {
            return getCamelotQuote(config, tokenIn, tokenOut, amount);
        } else if (routerType == RouterType.CHRONOS) {
            return getChronosQuote(config, tokenIn, tokenOut, amount);
        } else if (routerType == RouterType.ZYBERSWAP) {
            return getZyberSwapQuote(config, tokenIn, tokenOut, amount);
        } else if (routerType == RouterType.BEETHOVEN_X) {
            return getBeethovenXQuote(config, tokenIn, tokenOut, amount);
        } else if (routerType == RouterType.PLATYPUS) {
            return getPlatypusQuote(config, tokenIn, tokenOut, amount);
        } else if (routerType == RouterType.WOMBAT) {
            return getWombatQuote(config, tokenIn, tokenOut, amount);
        } else if (routerType == RouterType.GMXSWAP) {
            return getGMXQuote(config, tokenIn, tokenOut, amount);
        } else if (routerType == RouterType.MAVERICK) {
            return getMaverickQuote(config, tokenIn, tokenOut, amount);
        } else if (routerType == RouterType.ALGEBRA) {
            return getAlgebraQuote(config, tokenIn, tokenOut, amount);
        } else if (routerType == RouterType.AERODROME) {
            return getAerodromeQuote(config, tokenIn, tokenOut, amount);
        } else if (routerType == RouterType.ZEROX_PROTOCOL) {
            // 0x Protocol requires order book data, return 0 for now
            return 0; // Placeholder - would need order book integration
        }
        
        revert("Unsupported router type");
    }

    function getVelodromeQuote(
        RouterConfig memory config,
        address tokenIn,
        address tokenOut,
        uint256 amountIn
    ) internal view returns (uint256) {
        try IVelodromeRouter(config.routerAddress).getAmountsOut(
            amountIn,
            getVelodromeRoute(tokenIn, tokenOut)
        ) returns (uint[] memory amounts) {
            return amounts[amounts.length - 1];
        } catch {
            return 0;
        }
    }

    function getRamsesQuote(
        RouterConfig memory config,
        address tokenIn,
        address tokenOut,
        uint256 amountIn
    ) internal returns (uint256) {
        try IRamsesV3Quoter(config.routerAddress).quoteExactInputSingle(
            tokenIn,
            tokenOut,
            3000, // 0.3% fee tier
            amountIn,
            0
        ) returns (uint256 amountOut) {
            return amountOut;
        } catch {
            return 0;
        }
    }

    function getSolidlyQuote(
        RouterConfig memory config,
        address tokenIn,
        address tokenOut,
        uint256 amountIn
    ) internal view returns (uint256) {
        try ISolidlyRouter(config.routerAddress).getAmountsOut(
            amountIn,
            getSolidlyRoute(tokenIn, tokenOut)
        ) returns (uint[] memory amounts) {
            return amounts[amounts.length - 1];
        } catch {
            return 0;
        }
    }

    function getThenaQuote(
        RouterConfig memory config,
        address tokenIn,
        address tokenOut,
        uint256 amountIn
    ) internal view returns (uint256) {
        try IThenaRouter(config.routerAddress).getAmountsOut(
            amountIn,
            getThenaRoute(tokenIn, tokenOut)
        ) returns (uint[] memory amounts) {
            return amounts[amounts.length - 1];
        } catch {
            return 0;
        }
    }

    function getCamelotQuote(
        RouterConfig memory config,
        address tokenIn,
        address tokenOut,
        uint256 amountIn
    ) internal view returns (uint256) {
        address[] memory path = new address[](2);
        path[0] = tokenIn;
        path[1] = tokenOut;
        
        try ICamelotRouter(config.routerAddress).getAmountsOut(amountIn, path) returns (uint[] memory amounts) {
            return amounts[1];
        } catch {
            return 0;
        }
    }

    function getChronosQuote(
        RouterConfig memory config,
        address tokenIn,
        address tokenOut,
        uint256 amountIn
    ) internal view returns (uint256) {
        try IChronosRouter(config.routerAddress).getAmountsOut(
            amountIn,
            getChronosRoute(tokenIn, tokenOut)
        ) returns (uint[] memory amounts) {
            return amounts[amounts.length - 1];
        } catch {
            return 0;
        }
    }

    function getZyberSwapQuote(
        RouterConfig memory config,
        address tokenIn,
        address tokenOut,
        uint256 amountIn
    ) internal view returns (uint256) {
        address[] memory path = new address[](2);
        path[0] = tokenIn;
        path[1] = tokenOut;
        
        try IZyberSwapRouter(config.routerAddress).getAmountsOut(amountIn, path) returns (uint[] memory amounts) {
            return amounts[1];
        } catch {
            return 0;
        }
    }

    function getBeethovenXQuote(
        RouterConfig memory config,
        address tokenIn,
        address tokenOut,
        uint256 amountIn
    ) internal returns (uint256) {
        // Placeholder for Beethoven X (Balancer fork) quote
        return 0;
    }

    function getPlatypusQuote(
        RouterConfig memory config,
        address tokenIn,
        address tokenOut,
        uint256 amountIn
    ) internal view returns (uint256) {
        try IPlatypusPool(config.routerAddress).quotePotentialSwap(
            tokenIn,
            tokenOut,
            amountIn
        ) returns (uint256 potentialOutcome, uint256) {
            return potentialOutcome;
        } catch {
            return 0;
        }
    }

    function getWombatQuote(
        RouterConfig memory config,
        address tokenIn,
        address tokenOut,
        uint256 amountIn
    ) internal view returns (uint256) {
        try IWombatPool(config.routerAddress).quotePotentialSwap(
            tokenIn,
            tokenOut,
            int256(amountIn)
        ) returns (uint256 potentialOutcome, uint256) {
            return potentialOutcome;
        } catch {
            return 0;
        }
    }

    function getGMXQuote(
        RouterConfig memory config,
        address tokenIn,
        address tokenOut,
        uint256 amountIn
    ) internal view returns (uint256) {
        try IGMXRouter(config.routerAddress).getAmountOut(
            tokenIn,
            tokenOut,
            amountIn
        ) returns (uint256 amountOut, uint256) {
            return amountOut;
        } catch {
            return 0;
        }
    }

    function getMaverickQuote(
        RouterConfig memory config,
        address tokenIn,
        address tokenOut,
        uint256 amountIn
    ) internal view returns (uint256) {
        // Placeholder for Maverick concentrated liquidity quote
        return 0;
    }

    function getAlgebraQuote(
        RouterConfig memory config,
        address tokenIn,
        address tokenOut,
        uint256 amountIn
    ) internal returns (uint256) {
        try IAlgebraQuoter(config.routerAddress).quoteExactInputSingle(
            tokenIn,
            tokenOut,
            amountIn,
            0
        ) returns (uint256 amountOut) {
            return amountOut;
        } catch {
            return 0;
        }
    }

    function getAerodromeQuote(
        RouterConfig memory config,
        address tokenIn,
        address tokenOut,
        uint256 amountIn
    ) internal view returns (uint256) {
        return getVelodromeQuote(config, tokenIn, tokenOut, amountIn);
    }

    // Helper functions for Solidly-based DEXes
    function getVelodromeRoute(address tokenIn, address tokenOut) 
        internal 
        pure 
        returns (IVelodromeRouter.Route[] memory) {
        IVelodromeRouter.Route[] memory routes = new IVelodromeRouter.Route[](1);
        routes[0] = IVelodromeRouter.Route({
            from: tokenIn,
            to: tokenOut,
            stable: false
        });
        return routes;
    }

    function getSolidlyRoute(address tokenIn, address tokenOut) 
        internal 
        pure 
        returns (ISolidlyRouter.Route[] memory) {
        ISolidlyRouter.Route[] memory routes = new ISolidlyRouter.Route[](1);
        routes[0] = ISolidlyRouter.Route({
            from: tokenIn,
            to: tokenOut,
            stable: false
        });
        return routes;
    }

    function getThenaRoute(address tokenIn, address tokenOut) 
        internal 
        pure 
        returns (IThenaRouter.Route[] memory) {
        IThenaRouter.Route[] memory routes = new IThenaRouter.Route[](1);
        routes[0] = IThenaRouter.Route({
            from: tokenIn,
            to: tokenOut,
            stable: false
        });
        return routes;
    }

    function getChronosRoute(address tokenIn, address tokenOut) 
        internal 
        pure 
        returns (IChronosRouter.Route[] memory) {
        IChronosRouter.Route[] memory routes = new IChronosRouter.Route[](1);
        routes[0] = IChronosRouter.Route({
            from: tokenIn,
            to: tokenOut,
            stable: false
        });
        return routes;
    }
}

