// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

/**
 * @title MockDIAOracle
 * @notice Mock DIA Oracle for testing purposes
 */
contract MockDIAOracle {
    
    mapping(string => uint128) private prices;
    mapping(string => uint128) private timestamps;
    
    constructor() {
        // Set default price for IU2U/USD
        prices["IU2U/USD"] = 100000000; // $1.00 with 8 decimals
        timestamps["IU2U/USD"] = uint128(block.timestamp);
    }
    
    /**
     * @notice Set price for testing
     * @param key Price key (e.g., "IU2U/USD")
     * @param price Price with 8 decimals
     */
    function setPrice(string memory key, uint128 price) external {
        prices[key] = price;
        timestamps[key] = uint128(block.timestamp);
    }
    
    /**
     * @notice Get price data (implements DIA Oracle interface)
     * @param key Price key
     * @return price Price with 8 decimals
     * @return timestamp Last update timestamp
     */
    function getValue(string memory key) external view returns (uint128 price, uint128 timestamp) {
        return (prices[key], timestamps[key]);
    }
}

contracts/SwapCalldataGenerator.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./libraries/QuoteLibrary.sol";
import "./libraries/MulticallLibraryV2.sol";
import "./libraries/CalldataLibrary.sol";

interface IMulticall3Local {
    struct Call3 {
        address target;
        bool allowFailure;
        bytes callData;
    }
}

/**
 * @title SwapCalldataGenerator
 * @notice Helper contract to generate calldata for different DEX routers
 * @dev This contract helps generate the routerCalldata for cross-chain swaps
 */
contract SwapCalldataGenerator is Ownable {
    
    // Multicall3 address (same across most chains)
    address public constant MULTICALL3_ADDRESS = 0xcA11bde05977b3631167028862bE2a173976CA11;
    
    // Chain ID to router configurations  
    mapping(uint256 => mapping(QuoteLibrary.RouterType => QuoteLibrary.RouterConfig)) public routers;
    
    // Supported chains
    mapping(uint256 => bool) public supportedChains;
    
    // Curve protocol addresses per chain
    mapping(uint256 => address) public curveRegistries;
    
    // Balancer vault addresses per chain
    mapping(uint256 => address) public balancerVaults;
    
    // Chain -> token pair hash -> pool config
    mapping(uint256 => mapping(bytes32 => QuoteLibrary.PoolConfig)) public poolConfigs;
    
    event RouterConfigured(uint256 indexed chainId, QuoteLibrary.RouterType indexed routerType, address router);
    event ChainSupported(uint256 indexed chainId, bool supported);
    event CurveRegistryConfigured(uint256 indexed chainId, address registry);
    event BalancerVaultConfigured(uint256 indexed chainId, address vault);
    event PoolConfigured(uint256 indexed chainId, address tokenA, address tokenB, address pool, bytes32 poolId);

    constructor(address initialOwner) Ownable() {
        _transferOwnership(initialOwner);
    }

    /**
     * @notice Generate Uniswap V2 style swap calldata
     */
    function generateUniswapV2Calldata(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external pure returns (bytes memory) {
        return CalldataLibrary.generateUniswapV2Calldata(amountIn, amountOutMin, path, to, deadline);
    }

    /**
     * @notice Generate Uniswap V3 exact input swap calldata
     */
    function generateUniswapV3ExactInputCalldata(
        uint256 amountIn,
        uint256 amountOutMin,
        bytes calldata path,
        address recipient,
        uint256 deadline
    ) external pure returns (bytes memory) {
        return CalldataLibrary.generateUniswapV3ExactInputCalldata(amountIn, amountOutMin, path, recipient, deadline);
    }

    /**
     * @notice Generate Curve swap calldata (simplified implementation)
     */
    function generateCurveSwapCalldata(
        address tokenIn,
        address tokenOut,
        uint256 dx,
        uint256 minDy
    ) external pure returns (bytes memory) {
        return CalldataLibrary.generateCurveSwapCalldata(tokenIn, tokenOut, dx, minDy);
    }

    /**
     * @notice Generate 1inch style aggregated swap calldata
     */
    function generate1inchCalldata(
        address tokenIn,
        address tokenOut,
        uint256 amount,
        uint256 minReturn,
        bytes calldata data
    ) external pure returns (bytes memory) {
        return CalldataLibrary.generate1inchCalldata(tokenIn, tokenOut, amount, minReturn, data);
    }

    /**
     * @notice Generate ParaSwap V5 swap calldata
     */
    function generateParaSwapCalldata(
        address fromToken,
        uint256 fromAmount,
        uint256 toAmount,
        uint256 expectedAmount,
        address beneficiary,
        bytes calldata swapData
    ) external pure returns (bytes memory) {
        return CalldataLibrary.generateParaSwapCalldata(fromToken, fromAmount, toAmount, expectedAmount, beneficiary, swapData);
    }

    /**
     * @notice Generate 0x Protocol swap calldata
     */
    function generateZeroXCalldata(
        bytes32 orderHash,
        bytes calldata signature,
        uint128 takerTokenFillAmount
    ) external pure returns (bytes memory) {
        return CalldataLibrary.generateZeroXCalldata(orderHash, signature, takerTokenFillAmount);
    }

    /**
     * @notice Generate Kyber Network swap calldata
     */
    function generateKyberCalldata(
        address src,
        uint256 srcAmount,
        address dest,
        uint256 minConversionRate
    ) external pure returns (bytes memory) {
        return CalldataLibrary.generateKyberCalldata(src, srcAmount, dest, minConversionRate);
    }

    /**
     * @notice Generate DODO swap calldata
     */
    function generateDODOCalldata(
        address fromToken,
        address toToken,
        uint256 fromTokenAmount,
        uint256 minReturnAmount,
        address[] calldata dodoPairs,
        uint256 directions,
        bool isIncentive,
        uint256 deadLine
    ) external pure returns (bytes memory) {
        return CalldataLibrary.generateDODOCalldata(fromToken, toToken, fromTokenAmount, minReturnAmount, dodoPairs, directions, isIncentive, deadLine);
    }

    /**
     * @notice Generate Bancor swap calldata
     */
    function generateBancorCalldata(
        address[] calldata path,
        uint256 amount,
        uint256 minReturn,
        address beneficiary,
        address affiliateAccount,
        uint256 affiliateFee
    ) external pure returns (bytes memory) {
        return CalldataLibrary.generateBancorCalldata(path, amount, minReturn, beneficiary, affiliateAccount, affiliateFee);
    }

    /**
     * @notice Get router configuration for a chain and type
     */
    function getRouterConfig(
        uint256 chainId,
        QuoteLibrary.RouterType routerType
    ) external view returns (QuoteLibrary.RouterConfig memory config) {
        return routers[chainId][routerType];
    }

    /**
     * @notice Get Multicall3 address (same across all chains)
     */
    function getMulticall3Address() external pure returns (address multicall3Address) {
        return MULTICALL3_ADDRESS;
    }

    /**
     * @notice Get optimal router using individual calls (includes all router types)
     */
    function getOptimalRouterMulticall(
        uint256 chainId,
        address tokenIn,
        address tokenOut,
        uint256 amount
    ) external returns (address routerAddress, QuoteLibrary.RouterType routerType, uint256 expectedOutput) {
        return MulticallLibrary.getOptimalRouter(
            chainId,
            tokenIn,
            tokenOut,
            amount,
            supportedChains,
            routers,
            curveRegistries,
            balancerVaults,
            poolConfigs
        );
    }

    /**
     * @notice Get expected output amount from a specific router
     */
    function getExpectedOutput(
        uint256 chainId,
        QuoteLibrary.RouterType routerType,
        address tokenIn,
        address tokenOut,
        uint256 amount
    ) external returns (uint256 expectedOutput) {
        return QuoteLibrary.getExpectedOutput(
            chainId,
            routerType,
            tokenIn,
            tokenOut,
            amount,
            routers,
            curveRegistries,
            balancerVaults,
            poolConfigs
        );
    }

    /**
     * @notice Get quote from Uniswap V2 style router (external for try-catch)
     */
    function getUniswapV2Quote(
        address router,
        address tokenIn,
        address tokenOut,
        uint256 amountIn
    ) external view returns (uint256) {
        return QuoteLibrary.getUniswapV2Quote(router, tokenIn, tokenOut, amountIn);
    }

    // Admin functions

    /**
     * @notice Configure router for a specific chain
     */
    function configureRouter(
        uint256 chainId,
        QuoteLibrary.RouterType routerType,
        address routerAddress,
        bool isActive
    ) external onlyOwner {
        routers[chainId][routerType] = QuoteLibrary.RouterConfig({
            routerAddress: routerAddress,
            routerType: routerType,
            isActive: isActive
        });
        
        emit RouterConfigured(chainId, routerType, routerAddress);
    }

    /**
     * @notice Set chain support status
     */
    function setChainSupport(uint256 chainId, bool supported) external onlyOwner {
        supportedChains[chainId] = supported;
        emit ChainSupported(chainId, supported);
    }

    /**
     * @notice Batch configure multiple routers
     */
    function batchConfigureRouters(
        uint256[] calldata chainIds,
        QuoteLibrary.RouterType[] calldata routerTypes,
        address[] calldata routerAddresses,
        bool[] calldata activeStates
    ) external onlyOwner {
        require(
            chainIds.length == routerTypes.length &&
            routerTypes.length == routerAddresses.length &&
            routerAddresses.length == activeStates.length,
            "Array length mismatch"
        );

        for (uint256 i = 0; i < chainIds.length; i++) {
            routers[chainIds[i]][routerTypes[i]] = QuoteLibrary.RouterConfig({
                routerAddress: routerAddresses[i],
                routerType: routerTypes[i],
                isActive: activeStates[i]
            });
            
            emit RouterConfigured(chainIds[i], routerTypes[i], routerAddresses[i]);
        }
    }

    /**
     * @notice Configure Curve registry for a chain
     */
    function setCurveRegistry(uint256 chainId, address registryAddress) external onlyOwner {
        curveRegistries[chainId] = registryAddress;
        emit CurveRegistryConfigured(chainId, registryAddress);
    }

    /**
     * @notice Configure Balancer vault for a chain
     */
    function setBalancerVault(uint256 chainId, address vaultAddress) external onlyOwner {
        balancerVaults[chainId] = vaultAddress;
        emit BalancerVaultConfigured(chainId, vaultAddress);
    }

    /**
     * @notice Configure specific pool for token pair
     */
    function configurePool(
        uint256 chainId,
        address tokenA,
        address tokenB,
        address poolAddress,
        bytes32 poolId,
        bool isActive
    ) external onlyOwner {
        bytes32 pairHash = keccak256(abi.encodePacked(tokenA, tokenB));
        poolConfigs[chainId][pairHash] = QuoteLibrary.PoolConfig({
            poolAddress: poolAddress,
            poolId: poolId,
            isActive: isActive
        });
        
        emit PoolConfigured(chainId, tokenA, tokenB, poolAddress, poolId);
    }

    /**
     * @notice Batch configure multiple pools
     */
    function batchConfigurePools(
        uint256[] calldata chainIds,
        address[] calldata tokenAs,
        address[] calldata tokenBs,
        address[] calldata poolAddresses,
        bytes32[] calldata poolIds,
        bool[] calldata activeStates
    ) external onlyOwner {
        require(
            chainIds.length == tokenAs.length &&
            tokenAs.length == tokenBs.length &&
            tokenBs.length == poolAddresses.length &&
            poolAddresses.length == poolIds.length &&
            poolIds.length == activeStates.length,
            "Array length mismatch"
        );

        for (uint256 i = 0; i < chainIds.length; i++) {
            bytes32 pairHash = keccak256(abi.encodePacked(tokenAs[i], tokenBs[i]));
            uint256 chainId = chainIds[i];
            poolConfigs[chainId][pairHash] = QuoteLibrary.PoolConfig({
                poolAddress: poolAddresses[i],
                poolId: poolIds[i],
                isActive: activeStates[i]
            });
            
            emit PoolConfigured(chainIds[i], tokenAs[i], tokenBs[i], poolAddresses[i], poolIds[i]);
        }
    }
}

contracts/SwapCalldataUsageExample.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "./SwapCalldataGenerator.sol";
import "./libraries/QuoteLibrary.sol";
import "./libraries/CalldataLibrary.sol";
import "./interfaces/IIU2UGateway.sol";

/**
 * @title SwapCalldataUsageExample
 * @notice Example contract demonstrating how to use SwapCalldataGenerator for cross-chain swaps
 * @dev This contract shows practical usage patterns for generating and executing swap calldata
 */
contract SwapCalldataUsageExample {
    using SafeERC20 for IERC20;

    SwapCalldataGenerator public immutable calldataGenerator;
    IIU2UGateway public immutable iu2uGateway;
    
    // Events
    event SwapInitiated(
        address indexed user,
        address tokenIn,
        address tokenOut,
        uint256 amountIn,
        uint256 expectedAmountOut,
        string destinationChain,
        QuoteLibrary.RouterType routerType
    );
    
    event LocalSwapExecuted(
        address indexed user,
        address tokenIn,
        address tokenOut,
        uint256 amountIn,
        uint256 amountOut,
        QuoteLibrary.RouterType routerType
    );

    constructor(address _calldataGenerator, address _iu2uGateway) {
        calldataGenerator = SwapCalldataGenerator(_calldataGenerator);
        iu2uGateway = IIU2UGateway(_iu2uGateway);
    }

    /**
     * @notice Example: Execute a cross-chain swap using IU2U Gateway
     * @param tokenIn Input token address
     * @param tokenOut Output token address (on destination chain)
     * @param amountIn Amount of input tokens
     * @param destinationChain Name of destination chain
     * @param destinationAddress Recipient address on destination chain
     * @param routerType Type of DEX router to use
     * @param slippageBps Slippage tolerance in basis points (e.g., 300 = 3%)
     */
    function executeCrossChainSwap(
        address tokenIn,
        address tokenOut,
        uint256 amountIn,
        string calldata destinationChain,
        string calldata destinationAddress,
        QuoteLibrary.RouterType routerType,
        uint256 slippageBps
    ) external {
        require(amountIn > 0, "Amount must be > 0");
        require(slippageBps <= 1000, "Slippage too high"); // Max 10%

        // Transfer tokens from user
        IERC20(tokenIn).safeTransferFrom(msg.sender, address(this), amountIn);

        // Get destination chain ID (simplified - you'd need to implement this mapping)
        uint256 destinationChainId = _getChainId(destinationChain);
        require(destinationChainId > 0, "Unsupported destination chain");

        // Get router config for destination chain
        (address routerAddress, QuoteLibrary.RouterType configuredType, bool isActive) = calldataGenerator.routers(destinationChainId, routerType);
        require(isActive, "Router not active on destination chain");

        // Get quote for the swap (simplified for V2-style routers)
        uint256 expectedAmountOut = 0;
        if (routerType == QuoteLibrary.RouterType.UNISWAP_V2 || 
            routerType == QuoteLibrary.RouterType.SUSHISWAP ||
            routerType == QuoteLibrary.RouterType.PANCAKESWAP) {
            expectedAmountOut = calldataGenerator.getUniswapV2Quote(
                routerAddress,
                tokenIn,
                tokenOut,
                amountIn
            );
        }
        require(expectedAmountOut > 0, "Unable to get quote");

        // Calculate minimum amount out with slippage
        uint256 amountOutMin = (expectedAmountOut * (10000 - slippageBps)) / 10000;

        // Generate swap calldata (simplified - using Uniswap V2 style)
        address[] memory path = new address[](2);
        path[0] = tokenIn;
        path[1] = tokenOut;
        
        // Note: For cross-chain swaps, the recipient should be the destination contract
        // For this example, we'll use a placeholder address and let the relay handle it
        address tempRecipient = address(0x1234567890123456789012345678901234567890);
        
        bytes memory swapCalldata = calldataGenerator.generateUniswapV2Calldata(
            amountIn,
            amountOutMin,
            path,
            tempRecipient, // Placeholder - actual recipient handled by cross-chain protocol
            block.timestamp + 1800 // 30 minute deadline
        );

        // Approve IU2U Gateway to spend tokens
        IERC20(tokenIn).approve(address(iu2uGateway), amountIn);

        // Execute cross-chain swap through IU2U Gateway
        iu2uGateway.callContractWithToken(
            destinationChain,
            destinationAddress, // This is already a string
            swapCalldata,
            "IU2U", // Assuming we're swapping IU2U
            amountIn
        );

        emit SwapInitiated(
            msg.sender,
            tokenIn,
            tokenOut,
            amountIn,
            expectedAmountOut,
            destinationChain,
            routerType
        );
    }

    /**
     * @notice Helper function to map chain name to chain ID
     * @param chainName Name of the chain
     * @return chainId Chain ID
     */
    function _getChainId(string memory chainName) internal pure returns (uint256 chainId) {
        bytes32 nameHash = keccak256(bytes(chainName));
        
        if (nameHash == keccak256(bytes("ethereum"))) return 1;
        if (nameHash == keccak256(bytes("bsc"))) return 56;
        if (nameHash == keccak256(bytes("polygon"))) return 137;
        if (nameHash == keccak256(bytes("arbitrum"))) return 42161;
        if (nameHash == keccak256(bytes("optimism"))) return 10;
        if (nameHash == keccak256(bytes("avalanche"))) return 43114;
        if (nameHash == keccak256(bytes("crossfi"))) return 4158;
        
        return 0; // Unsupported chain
    }

    /**
     * @notice Helper function to get router address (simplified for demonstration)
     * @param chainId Chain ID
     * @param routerType Router type
     * @return routerAddress Address of the router
     */
    function _getRouterAddress(uint256 chainId, QuoteLibrary.RouterType routerType) 
        internal 
        pure 
        returns (address routerAddress) 
    {
